import React, { Fragment, useEffect, useState } from 'react'
import { getUserBookings } from '../Api Helper/apiHelp';
import { Box, List, ListItem, ListItemText, Typography } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const UserProfile = () => {
    const [bookings,setBookings]=useState();
    useEffect(()=>{
        getUserBookings()
        .then((res)=>setBookings(res.Booking))
        .catch((err)=>console.log(err))
    },[]);
    console.log(bookings);
  return (
    <Box width={'100%'} display={'flex'}>
      {bookings && bookings.length > 0 && (<Fragment>{" "}<Box flexDirection={'column'} justifyContent={'center'} alignItems={'center'} width={'30%'}>
      <AccountCircleIcon sx={{fontSize:'10rem',textAlign:'center',ml:3}}/>
      <Typography mt={1} padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>Hi, {bookings[0].user.name}</Typography>
      <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>Email: {bookings[0].user.email}</Typography>
      </Box></Fragment>)}
      <Box width={'70%'} display={'flex'}>
        <Typography variant='h3' fontFamily={'verdana'} textAlign={'center'} padding={2}>Bookings</Typography>
        <Box margin={'auto'} display={'flex'} flexDirection={'column'} width={'80%'}>
          <List>{bookings.map((booking,index)=>{
            <ListItem sx={{bgcolor:'#00d386',color:'white',textAlign:'center',margin:1}}>
              <ListItemText sx={{margin:1,width:'auto',textAlign:'left'}}>
                Movie:{booking.movie.title}
              </ListItemText>
              <ListItemText sx={{margin:1,width:'auto',textAlign:'left'}}>
               Seat no. {booking.seatno}
              </ListItemText>
            </ListItem>
          })}</List>
        </Box>
      </Box>
      
    </Box>
  )
}

export default UserProfile
