import React, { useState,useEffect } from "react";
import { AppBar, Autocomplete, Tab, Tabs, TextField } from '@mui/material';
import { Box, Toolbar } from '@mui/material';
import MovieIcon from '@mui/icons-material/Movie';
import getAllMovies from '../Api Helper/apiHelp';
import { Link } from "react-router-dom";
// import Link from "@mui/material";
// import  from '@mui/system';


const Header = () => {
    const [value,setValue]=useState(0);
    const [movie,setMovie]=useState([]);
    useEffect(()=>{getAllMovies().then(data =>setMovie(data.data)).catch(err => console.log(err))},[])
    return (<>
        <AppBar position="sticky" sx={{bgcolor:"#2b2d42"}}>
            <Toolbar>
                <Box width={"20%"}><MovieIcon /></Box>
                <Box width={"50%"} margin={"auto"}>
                    <Autocomplete id="free-solo-demo" freeSolo options={movie.map((option) => option.title)} renderInput={(params) => (<TextField sx={{input:{color:"white"}}} {...params} label="Search Across Multiple Movies" />)} />
                </Box>
                <Box display={"flex"}>
                    <Tabs textColor="inherit" indicatorColor="secondary" value={value} onChange={(e,val)=>setValue(val)}>
                        <Tab LinkComponent={Link} to="/movies" label="Movies" />
                        <Tab LinkComponent={Link} to="/admin"label="Admin" />
                        <Tab LinkComponent={Link} to="/auth" label="Auth" />
                    </Tabs>
                </Box>
            </Toolbar>
        </AppBar>
    </>
    )
}

export default Header
