import { Box, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import getAllMovies from "../../Api Helper/apiHelp";
import MovieItem from "./MovieItem";

const Movies = () => {
  const [movies, setMovies] = useState();
  useEffect(() => {
    getAllMovies()
      .then((data) => setMovies(data.data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <Box margin={"auto"} marginTop={4}>
      <Typography margin={"auto"} variant="h4" padding={2} width="40%" bgcolor={"#900C3F"} color="white" textAlign={"center"}>
        All Movies
      </Typography>
      <Box width={"100%"} margin="auto" marginTop={5} display={"flex"} justifyContent="center" flexWrap={"wrap"}>
        {movies && movies.map((movie, index) => (<MovieItem key={index} id={movie._id} posterUrl={movie.posterUrl} releaseDate={movie.releaseDate} title={movie.title}/>))}
      </Box>
    </Box>
  );
};

export default Movies;
