import { Box, Button, Checkbox, FormLabel, TextField, Typography } from '@mui/material'
import React,{useState} from 'react'
import { addMovie } from '../../Api Helper/apiHelp'

const labelProps={
  mt:1,
  mb:1
}

const AddMovie = () => {
  const [inputs,setInputs]=useState({title:"",description:"",posterUrl:"",releaseDate:"",featured:false})
  const [actors,setActors]=useState([""]);
  const [actor,setActor]=useState();

  const handleChange=(e)=>{
    setInputs((prevState)=>({...prevState,[e.target.name]:e.target.value}))
  }
  const handleSubmit=(e)=>{
    e.preventDefault();
    console.log(inputs,actors);
    addMovie({...inputs,actors})
    .then((res)=>console.log(res)
    .catch((err)=>console.log(err))
    )
  }

  return (
    <div><form onSubmit={handleSubmit}>
      <Box width={'50%'} padding={10} margin='auto' display={'flex'} flexDirection={'column'} boxShadow={'10px 10px 20px #ccc'}>
        <Typography textAlign={'center'} varient='h5' fontFamily={'verdana'}>
          Add New Movie
        </Typography>
        <FormLabel sx={labelProps}>Title</FormLabel>
        <TextField name="title" variant='standard' margin='normal' value={inputs.title} onChange={handleChange}/>
        <FormLabel sx={labelProps}>Description</FormLabel>
        <TextField name="description" variant='standard' margin='normal' value={inputs.description} onChange={handleChange}/>
        <FormLabel sx={labelProps}>Poster Url</FormLabel>
        <TextField name="posterUrl" variant='standard' margin='normal' value={inputs.posterUrl} onChange={handleChange}/>
        <FormLabel sx={labelProps}>Release Date</FormLabel>
        <TextField type='date' name="releaseDate" variant='standard' margin='normal' value={inputs.releaseDate} onChange={handleChange}/>
        <FormLabel sx={labelProps}>Actor</FormLabel>
        <Box display={'flex'}>
        <TextField value={actor} name="actor" onChange={(e)=>setActor(e.target.value)} variant='standard' margin='normal'/>
        <Button onClick={()=>{setActors([...actors,actor]);
          setActor("");}}>Add</Button>
        </Box>
        <FormLabel>Featured</FormLabel>
        <Checkbox name="featured" checked={inputs.featured} onClick={(e) =>
              setInputs((prevSate) => ({
                ...prevSate,
                featured: e.target.checked,
              }))
            } sx={{mr:'auto'}}/>
        <Button type='submit' variant='contained' sx={{width:'30%',margin:'auto',bgcolor:'#2b2d42',":hover":{bgcolor:"#121217"}}}>Add New Movie</Button>
        </Box>
        </form>
        </div> 
  )
}

export default AddMovie
