import React, { Fragment, useEffect, useState } from 'react'
import { getUserBookings, getUserDetails } from '../Api Helper/apiHelp';
import { Box, IconButton, List, ListItem, ListItemText, Typography } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import DeleteIcon from '@mui/icons-material/Delete';
import { deleteBooking } from '../Api Helper/apiHelp';

const UserProfile = () => {
    const [bookings,setBookings]=useState([]);
    const [user,setUser]=useState([]);
    useEffect(()=>{
        getUserBookings()
        .then((data)=>setBookings(data.bookings))
        .catch((err)=>console.log(err))

        getUserDetails()
        .then((res)=>setUser(res.user))
        .catch((err)=>console.log(err));
    },[]);
    console.log(bookings);
    const handleDelete=(id)=>{
      deleteBooking(id)
      .then((res)=>console.log(res))
      .catch((err)=>console.log(err));      
    }
  return (
    <Box width={'100%'} display={'flex'}>   
      {/* <Fragment>{" "}{user && (<Box flexDirection={'column'} justifyContent={'center'} alignItems={'center'} width={'30%'}> 
      <AccountCircleIcon sx={{fontSize:'10rem',textAlign:'center',ml:3}}/>
      {user.map((user)=>{<><Typography mt={1} padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>Hi, {user.name}</Typography>
      <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>Email: {user.email}</Typography>
      </>})}
      </Box>)}</Fragment> */}
      
      {bookings && (<Box width={'70%'} display={'flex'}>
        <Typography variant='h4' fontFamily={'verdana'} textAlign={'center'} padding={2}>{bookings<2?"Booking":"Bookings"}</Typography>
        <Box margin={'auto'} display={'flex'} flexDirection={'column'} width={'80%'}>
          <List>{bookings.map((booking,index)=>{
            <ListItem sx={{bgcolor:'#00d386',color:'white',textAlign:'center',margin:1}}>
              <ListItemText sx={{margin:1,width:'auto',textAlign:'left'}}>
                Movie:{booking.movie.title}
              </ListItemText>
              <ListItemText sx={{margin:1,width:'auto',textAlign:'left'}}>
               Seat no: {booking.seatno}
              </ListItemText>
              <ListItemText sx={{margin:1,width:'auto',textAlign:'left'}}>
               Date: {new Date(booking.date).toDateString()}
              </ListItemText>
              <IconButton onClick={()=>handleDelete(booking._id)} color='error'>
              <DeleteIcon color='red'/>
              </IconButton>
            </ListItem>
          })}</List>
        </Box>
      </Box>)}
      
    </Box>
  )
}

export default UserProfile
