import React, { Fragment, useEffect, useState } from 'react'
import { getUserBookings } from '../Api Helper/apiHelp';
import { Box, Typography } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const UserProfile = () => {
    const [bookings,setBookings]=useState();
    useEffect(()=>{
        getUserBookings()
        .then((res)=>setBookings(res.booking))
        .catch((err)=>console.log(err))
    },[]);
    console.log(bookings);
  return (
    <Box width={'100%'} display={'flex'}>
      {bookings && bookings.length > 0 && (<Fragment>{" "}<Box flexDirection={'column'} justifyContent={'center'} alignItems={'center'} width={'30%'}>
      <AccountCircleIcon sx={{fontSize:'10rem'}}/>
      <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>Hi, {bookings[0].user.name}</Typography>
      </Box></Fragment>)}
      <Box width={'70%'}></Box>
      
    </Box>
  )
}

export default UserProfile
