import { Box, Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'
import getAllMovies from '../../Api Helper/apiHelp';
import MovieItem from './MovieItem';

const Movie = () => {
  const [movie,setMovie]=useState();
  useEffect(()=>{
    getAllMovies().then((data)=>setMovie(data.movies)).catch((err)=>console.log(err))
  },[])
  return (
   <Box margin={"auto"} marginTop={4}>
    <Typography variant='h4' padding={2} width="100%" bgcolor={"#900C3F"} color={"white"} textAlign="center">All Movies</Typography>
    <Box display={"flex"} width={"40%"} justifyContent={"center"} flexWrap={"wrap"}>
      {movie && movie.slice(0,4).map((movie,index)=><MovieItem id={movie.id} posterUrl={movie.posterUrl} releaseDate={movie.releaseDate} title={movie.title} key={index}/>)}
      </Box>
   </Box>
  )
}

export default Movie
