import axios from 'axios';

export const getAllMovies=async()=>{
    const res=await axios.get("movie/")
    .catch(err=>{
        console.log(err);
    })
    if (res.status ===! 200) {
        console.log("No data");
    }
    const movie = res.data;
    return movie;
}

export const sendUserAuthReq=async(data,signup)=>{
    const res=await axios.post(`/user/${signup ? "register" : "login"}`,{
        name:signup?data.name:"",
        email:data.email,
        password:data.password,
        number:signup?data.number:""
    })
    .then((res)=>console.log(res))
    .catch((err)=>console.log(err))

    if (res.status===!200) {
        console.log("Unexpected Error Occurred");
    }
    const resData=res.data;
    return resData;
}


export const sendAdminAuth=async(data)=>{
  const res = await axios.post('/admin/SignIn',{email:data.email,password:data.password})
  .catch((err)=>console.log(err));
   
  if (res.status===!200) {
    console.log("Unexpected Error Occurred");
}

const resData= await res.data;
return resData;
  
}

export const getMovieDetails=async(id)=>{
    const res=await axios.get(`/movie/${id}`)
    .catch((err)=>console.log(err));
   
    if (res.status===!200) {
      console.log("Unexpected Error Occurred");
  }
  
  const resData= await res.data;
  return resData;
}

export const newBooking= async(data)=>{
    const res=await axios.post('/booking',{movie:data.movie,seatno:data.seatno,date:data.date,user:localStorage.getItem("user")})
    .catch((err)=>{console.log(err)});

    if (res.status===!200) {
        return console.log("Unexpected Error");
    }

    const resData= await res.data;
    return resData;
}

export const getAllBookings=async()=>{
    const id=localStorage.getItem('user');
    const res = axios.get(`/user/booking/${id}`)
    .catch((err)=>{console.log(err)});

    if (res.status===!200) {
        return console.log("Unexpected Error");
    }

    const resData= await res.data;
    return resData;
}
