import { Box, Button, Dialog, FormLabel, IconButton, TextField, Typography } from '@mui/material'
import React, { useState } from 'react';
import CloseRoundedIcon from '@mui/icons-material/CloseRounded';

const AuthForm = () => {
  const [inputs,setInputs]=useState()
  const [signUp, setSignUp] = useState(false)
  return (
    <Dialog PaperProps={{ style: { borderRadius: 20 } }} open={true}>
      
      <Typography variant='h4' textAlign={"center"}>
      {signUp?"SignUp":"Login"}<Box sx={{ ml: 'auto', padding: 1 }}>
        <IconButton><CloseRoundedIcon /></IconButton>
      </Box>
      </Typography>
      <form>
        <Box padding={4} display={'flex'} justifyContent={'center'} flexDirection={'column'} width={400} height={400} margin={'auto'} alignContent={'center'}>
          {signUp && (<>{" "}
            <FormLabel >Name</FormLabel>
            <TextField margin='normal' variant='standard' type='email' name='name' />
          </>)}
          <FormLabel >Email</FormLabel>
            <TextField margin='normal' variant='standard' type='email' name='email' />
          <FormLabel >Password</FormLabel>
          <TextField margin='normal' variant='standard' type='password' name='password' />
          {signUp && (<>{" "}
            <FormLabel sx={{mb:1}}>Phone No.</FormLabel>
            <TextField margin='normal' variant='standard' type='text' name='number' />
          </>)}
          <Button sx={{ mt: 2, borderRadius: 10, bgcolor: "#2b2d42" }} type='submit' fullWidth variant='contained'>{signUp?"SignUp":"Login"}</Button>
          <Button onClick={()=>{setSignUp(!signUp)}} sx={{ mt: 2, borderRadius: 10 }} fullWidth >{signUp?"Login":"SignUp"}</Button>
        </Box>
      </form>
    </Dialog>
  )
}

export default AuthForm
